import React from "react";
import axios from "axios";
import Nav from "./navbar";
impo

export class DetalleProducto extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      producto: [],
    };
  }

  async componentDidMount() {
    const producto = await axios.get(
      `http://localhost:5000/producto/search/${this.props.match.params.id}`
    );
    this.setState({ producto: producto.data });
    console.log(this.state)
  }

  render() {
    return (
    <>
        <Nav />
        <div className="container-productos" id="productos-container">        
            <div className="warpper">
               <img src={`/${this.state.producto.imagen}`}></img>
            </div>
        </div>       
    </>
    );
  }
}

export default DetalleProducto;
