import React from "react";
import axios from "axios";
import Nav from "./navbar";
import './styles/detalleProducto.css';

export class Carrito extends React.Component {

    constructor (props) {
        super(props);
        this.state = {
            productos : []
        };
    };

    async componentDidMount() {
        const producto = JSON.parse(localStorage.getItem('producto'));         
        this.setState({productos : producto});
    };

    render() {
        const renderCarrito = () => {
            if (this.state.productos === null){
                return <h1>Carrito Vacio</h1>
            } else {
                <div>
                return this.state.productos.map((producto, index) => (
                    <div className="card" key={index}> 
                        <div className="row">
                            <div className="col-md-7">
                                <ul>
                                    <li>Nombre: {producto[0].nombre}</li>
                                    <li>Referencia: {producto[0].referencia}</li>
                                    <li>Cantidad: {producto[0].cantidad}</li>
                                    <li>Precio: {producto[0].precio}</li>
                                    <li>Total : {producto[0].cantidad * producto[0].precio}</li>
                                </ul>
                            </div>

                        </div>
                    </div>
                ))
                <div className="col-md-3">

                </div>
                </div>
            }
        }
        return (
            <>
                <Nav/>
                <div className="container-producto">
                    <div className="row">                        
                            {renderCarrito()}
                    </div>
                </div>
            </>
        );
    };
};

export default Carrito;
