import React from "react";
import axios from "axios";
import Nav from "./navbar";
import './styles/detalleProducto.css';

export class DetalleProducto extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      producto: [],
    };
  }

  async componentDidMount() {
    const producto = await axios.get(
      `http://localhost:5000/producto/search/${this.props.match.params.id}`
    );
    this.setState({ producto: producto.data });
    console.log(this.state)
  }

  render() {
    return (
    <>
        <Nav />
        <div className="container-productos container" id="productos-container">        
            <div className="warpper">
               <div className="row">
                <div className= "col-md-3 pb-0 pb-md-0">
                  <div className= "card-body">
                    <img className="card-img-top" src ={`/${this.state.producto.imagen}`} />				
                  </div>
                </div>	
                <div className= "col-md-3 pb-1 pb-md-0">
                  <div className= "card-body">
                    <h5 className="card-text">{this.state.producto.nombre}</h5>
                    <p className="card-text">{this.state.producto.precio}</p>
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.</p>
                    <input id="value" type= "number" min="1" max="10"/>
                    <button className="btn btn-success btn-outline-dark">add to car</button>
                  </div>
                </div>
               </div>
            </div>
        </div>       
    </>
    );
  }
}

export default DetalleProducto;
