import React from 'react';
import './styles/editarPerfil.css';
import Nav from "./navbar";

export class EditarPerfil extends React.Component {
    handleSubmit = async(e) => {
        e.preventDefault();
    
        const datos = {          
          nombres : this.nombres,
          apellidos : this.apellidos,
          identificacion : this.identificacion ,
          correo : this.correo,
          contraseña : this.contraseña
        };
    
        const user = await axios.put(`http://localhost:5000/usuario/${this.correo}`);
        
        if (user.data === this.correo) {
          console.log('El usuario ya existe');
        } else {
         axios.post('http://localhost:5000/signup', datos);
        }
       
      };
    render() {
        return (
            <>
            <Nav/>
            <fomr handleSubmit={this.handleSubmit}>
            <div className="container-productos mt-5">
                <div className="col-md-5">      
                    <div className="card-editar" >
                        <div className="warpper-editar">
                            <div className="card_img-editar">
                                <img src={sessionStorage.getItem('imagen')}/>
                            </div>   
                            <div className="row">
                                <div className="col-md-12">
                                    <div className="form-group">
                                        <input  type="file" name="image"/>
                                        <button type="submit">Subir</button>
                                    </div>
                                </div>						
					        </div>                               
                        </div>
                    </div>
                 </div>

                <div className="col-md-5"> 
                    <h1>Actializa tus datos</h1>        
                    <div className="input-field col s12">
                        <input
                        type="text"
                        name="nombres"
                        className="form-control"
                        defaultValue={sessionStorage.getItem('nombres')}
                        />
                    </div>
                    <div className="input-field col s12">
                        <input
                        type="text"
                        name="apellidos"
                        className="form-control"
                        defaultValue={sessionStorage.getItem('apellidos')}
                        />
                    </div>
                    <div className="input-field col s12">
                        <input
                        type="email"
                        name="correo"
                        className="form-control"
                        defaultValue={sessionStorage.getItem('correo')}
                        />
                    </div>
                    <div className="input-field col s12">
                        <input
                        type="text"
                        name="nombre"
                        className="form-control"
                        defaultValue={sessionStorage.getItem('identificacion')}
                        />
                    </div>
                    <div className="">
                        <button type="submit" className="ml-2">
                        Actializar datos
                        </button>
                        <br/>
                        <button type="submit" className="mt-3" href="./perfil">Cancelar
                        </button>
                     </div>
                </div>    
            </div>
            </fomr>
            </>
        );
    };
};

export default EditarPerfil;
