import React from "react";

export class Signup extends React.Component {
  constructor(props) {
    super(props);
  }

  handleSubmit = async(e) => {
    e.preventDefault();

    const datos = {
      nombre : this.nombre,
      apellidos : this.apellidos,
      correo : this.correo,
      contraseña : this.contraseña
    };

    const user = await axios.get(`http://localhost:5000/login/${this.correo}`);
    if (user.data === null) {
      document.getElementById("errorEmail").innerHTML = 'El usaurio no es valido, intente de nuevo.';
    } else {
      axios.post('http://localhost:5000/login', datos)
      .then( res => {
        if(res.data.message === 'Contrsaeña incorrecta'){
          document.getElementById("errorPass").innerHTML = 'La contraseña no es valida, intente de nuevo.';        
        } else {
          console.log("Contraseña correct")
        };       
    })
    .catch( err => {
      console.log(err);
    });
    };
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <h1>Create account</h1>
        <div className="social-container">
          <a href="#" className="social"><i className="fab fa-facebook-f"></i></a>
          <a href="#" className="social"><i className="fab fa-google-plus-g"></i></a>
          <a href="#" className="social"><i className="fab fa-linkedin-in"></i></a>
        </div>
        <span>Or use your mail for registration</span>
        <input type="text" placeholder="Names"  onChange={(e) => (this.nombre = e.target.value)}/>
        <input type="text" placeholder="Surnames" onChange={(e) => (this.apellido = e.target.value)}/>
        <input type="email" placeholder="Email" onChange={(e) => (this.correo = e.target.value)}/>
        <input type="password" placeholder="Password" onChange={(e) => (this.contraseña = e.target.value)}/>                    
        <button type="submit">Sign up</button>
    </form>
    );
  }
}

export default Signup;
