import React from "react";
import axios from "axios";
import Nav from "./navbar";
import './styles/detalleProducto.css';

export class Carrito extends React.Component {

    constructor (props) {
        super(props);
        this.state = {
            productos : []
        };
    };

    async componentDidMount() {
        const producto = JSON.parse(localStorage.getItem('producto'));        
        
        for (let i = 0; i < producto.length; i++){
            this.setState({productos : producto});
        }
        console.log(producto.length);   
        console.log(this.state.productos.length);
    };

    render() {
        const renderCarrito = () => {
            if (this.state.productos === null){
                return <h1>Carrito Vacio</h1>
            } else {
                return this.state.productos.map((producto, index) => (
                    <div className="card" key={index}>                        
                        <div className="warpper">
                            <div className="color_bg batman"></div>
                            <div className="card_img">
                                <img src={producto.imagen}></img>
                            </div>
                            <div className="heart">
                                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 64 64">
                                    <path d="M47 5c-6.5 0-12.9 4.2-15 10-2.1-5.8-8.5-10-15-10A15 15 0 0 0 2 20c0 13 11 26 30 39 19-13 30-26 30-39A15 15 0 0 0 47 5z">
                                    </path>
                                </svg>
                            </div>
                            <div className="cardInfo">
                                <h1>{producto.nombre}</h1>
                                <p className="date_">{}</p>
                                <div className="action">
                                    <div className="priceGroup">
                                        <p className="price old_price">{producto[0].nombre}</p>
                                        <p className="price new_price">{producto.precio}</p>                                                            
                                    </div>                                    
                                    <div className="cart">
                                        <svg className="outCart" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 64 64">
                                            <path d="M2 6h10l10 40h32l8-24H16"></path>
                                            <circle cx="23" cy="54" r="4"></circle>
                                            <circle cx="49" cy="54" r="4"></circle>
                                        </svg>
                                     </div>                                    
                                </div>
                                <a href={`./detalle-producto/${producto._id}`}>
                                    <button>Details</button>
                                </a>
                            </div>
                        </div>                        
                    </div>
                ))
            }
        }
        return (
            <>
                <Nav/>
                <div className="container-producto">
                    <div className="row">                        
                            {renderCarrito()}
                    </div>
                </div>
            </>
        );
    };
};

export default Carrito;
