import React from "react";
import axios from "axios";
import Nav from "./navbar";

export class DetalleProducto extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      producto: [],
    };
  }

  async componentDidMount() {
    const producto = await axios.get(
      `http://localhost:5000/producto/search/${this.props.match.params.id}`
    );
    this.setState({ producto: producto.data });
    console.log(this.state);
  }

  render() {
    return (
      <>
        <Nav />
        <div className="container-productos" id="productos-container">
          <div className="warpper">
            <div className="row">
              <div class="col-md-5 pb-1 pb-md-0">
                <div class="card-body">
                  <img class="card-img-top" src="${img}" />
                </div>
              </div>
              <div class="col-md-5 pb-1 pb-md-0">
                <div class="card-body">
                  <h5 class="card-text">${this.state.producto.nombre}</h5>
                  <p class="card-text">${this.state.producto.precio}</p>
                  <p></p>
                  <input id="value" type="number" min="1" max="10" />
                  <button class="btn btn-success btn-outline-dark">
                    add to car
                  </button>
                </div>
              </div>
            </div>
            <img src={`/${this.state.producto.imagen}`}></img>
          </div>
        </div>
      </>
    );
  }
}

export default DetalleProducto;
