import React from "react";
import axios from "axios";
import Nav from "./navbar";
import 'bootstrap/dist/css/bootstrap.min.css';

export class DetalleProducto extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      producto: [],
    };
  }

  async componentDidMount() {
    const producto = await axios.get(
      `http://localhost:5000/producto/search/${this.props.match.params.id}`
    );
    this.setState({ producto: producto.data });
    console.log(this.state)
  }

  render() {
    return (
    <>
        <Nav />
        <div className="container">
        <ul>
        
            <div className="card text-center bg-dark animate__animated animate__fadeInUp">
              <div className="overflow">
                <img
                  src={`/${this.state.producto.imagen}`}
                  alt="a wallpaper"
                  className="card-img-top"
                />
              </div>
              <div className="card-body text-light">
                <h4 className="card-title">{this.state.producto.nombre}</h4>
                <p className="card-text text-secondary">
                  {text
                    ? text
                    : "Lorem ipsum dolor sit amet consectetur, adipisicing elit. Magnam deserunt fuga accusantium excepturi quia, voluptates obcaecati nam in voluptas perferendis velit harum dignissimos quasi ex? Tempore repellat quo doloribus magnam."}
                </p>
                <a
                  href={url ? url : "#!"}
                  target="_blank"
                  className="btn btn-outline-secondary border-0"
                  rel="noreferrer"
                >
                  Go to {title}
                </a>
              </div>
            </div>;
        
        </ul>
      </div>   
    </>
    );
  }
}

export default DetalleProducto;
