import React from "react";
import axios from "axios";
import Nav from "./navbar";

export class DetalleProducto extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      producto: [],
    };
  }

  async componentDidMount() {
    const producto = await axios.get(
      `http://localhost:5000/producto/search/${this.props.match.params.id}`
    );
    this.setState({ producto: producto.data });
    console.log(this.state)
  }

  render() {
    return (
    <>
        <Nav />
        <div className="container-productos" id="productos-container">        
            <div className="warpper">
                    <div class= "col-md-5 pb-1 pb-md-0">
				        <div class= "card-body">
					        <img class="card-img-top" src ={this.state.imagen} />				
				        </div>
			        </div>	
		            <div class= "col-md-5 pb-1 pb-md-0">
			            <div class= "card-body">
			                <h5 class="card-text">${this.state.nombre}</h5>
			                <p class="card-text">${this.state.precio}</p>
				            <p>{this.state.descripcion}</p>
				            <input id="value" type= "number" />
			  	            <button class="btn btn-success btn-outline-dark">add to car</button>
		                </div>
                    </div>
            </div>
        </div>       
    </>
    );
  }
}

export default DetalleProducto;
